import { useState } from "react";
import { useWorkoutsContext } from "../hooks/useWorkoutsContext";
import { useAuthContext } from "../hooks/useAuthContext";

const WorkoutForm = ({ workout, setWorkoutToEdit}) => {
  const { dispatch } = useWorkoutsContext();
  const [title, setTitle] = useState("");
  const [load, setLoad] = useState("");
  const [reps, setReps] = useState("");
  const [error, setError] = useState(null);
  const [emptyFields, setEmptyFields] = useState([]);
  const { user } = useAuthContext();

  const handleSubmit = async (e) => {
    e.preventDefault();
    if(!user){
        setError('You must be looged in')
        return 
    }
    const workout = { title, load, reps };
    const response = await fetch("/api/workouts", {
      method: "POST",
      body: JSON.stringify(workout),
      headers: {
        "Content-Type": "application/json",
        "Authorization": `Bearer ${user.token}`
      },
    });
    const json = await response.json();
    if (!response.ok) {
      setError(json.error);
      setEmptyFields(json.emptyFields);
    }
    if (response.ok) {
      setTitle("");
      setLoad("");
      setReps("");
      setError(null);
      setEmptyFields([]);
      console.log("new workout added", json);
      if (workoutToEdit) {
        dispatch({
            type: "UPDATE_WORKOUT",
            payload: json,
        });
        setWorkoutToEdit(null); // Reset the edit mode
    } else {
        dispatch({
            type: "CREATE_WORKOUT",
            payload: json,
        });
    }
    }
  };
  useEffect(() => {
    if (workoutToEdit) {
        setTitle(workoutToEdit.title);
        setLoad(workoutToEdit.load);
        setReps(workoutToEdit.reps);
    }
}, [workoutToEdit]);
  return (
    <form className="create" onSubmit={handleSubmit}>
      <h3>Add a New To Do Item</h3>

      <label>Title:</label>
      <input
        type="text"
        onChange={(e) => {
          setTitle(e.target.value);
        }}
        value={title}
        className={emptyFields.includes("title") ? "error" : ""}
      />

<label>Description:</label>
      <textarea
        onChange={(e) => setLoad(e.target.value)} // Use textarea instead of input
        value={load}
        className={emptyFields.includes("load") ? "error" : ""}
        rows="4" // You can adjust the number of rows as needed
        cols="50" // You can adjust the width of the textarea
      ></textarea>

      <label>Priority:</label>
      <input
        type="number"
        onChange={(e) => {
          setReps(e.target.value);
        }}
        value={reps}
        className={emptyFields.includes("reps") ? "error" : ""}
      />
      <button>Add +</button>
      {error && <div className="error">{error}</div>}
    </form>
  );
};

export default WorkoutForm;
