import { useWorkoutsContext } from '../hooks/useWorkoutsContext';
import { useAuthContext } from "../hooks/useAuthContext";

const WorkoutDetails = ({ workout, setWorkoutToEdit }) => {
    const { user } = useAuthContext();
    const { dispatch } = useWorkoutsContext();

    const handleEdit = () => {
        if (setWorkoutToEdit) {
            setWorkoutToEdit(workout); // Set workout to be edited in the parent component (Home)
        }
    };

    const handleDelete = async () => {
        if (!user) return;
        
        const response = await fetch(`/api/workouts/${workout._id}`, {
            method: 'DELETE',
            headers: {
                'Authorization': `Bearer ${user.token}`
            }
        });

        if (response.ok) {
            dispatch({ type: 'DELETE_WORKOUT', payload: workout });
        }
    };

    return (
        <div className="workout-details">
            <div className="content">
                <h4>{workout.title}</h4>
                <p><strong>Description: </strong>{workout.load}</p>
                <p><strong>Priority: </strong>{workout.reps}</p>
            </div>
            <div className="buttons">
                <span className="material-symbols-outlined" onClick={handleDelete}>delete</span>
                <span className="material-symbols-outlined" onClick={handleEdit}>edit</span>
            </div>
        </div>
    );
};

export default WorkoutDetails;
